import React, { Component } from 'react'
import Aux from '../../hoc/Aux'
import Burger from '../../components/Burger/Burger'
import BurgerControls from '../../components/Burger/BuildControls/BuildControls'


const IngredientCosts = {
    salad : 0.4,
    bacon : 1.2,
    cheese : 0.4,
    meat : 1.4
}


class BurgerBuilder extends Component {
    state = {
        ingredients: {
            salad: 0,
            bacon: 0,
            cheese: 0,
            meat: 0
        },
        cost: 7 
    }

    IngredientAdded = (type) => {
        const OldCount = this.state.ingredients[type];
        const UpdatedCount = this.OldCount+1;

        const updatedIngredientCost = { ...this.state.ingredients}
        updatedIngredientCost[type] = UpdatedCount;

        const Oldcost = this.state.cost;
        const NewCost = this.Oldcost+ this.IngredientCost[type];

        this.setState({cost : NewCost, ingredients: updatedIngredientCost });
    }

    IngredientReduced = (type) => {
        const OldCount = this.state.ingredients[type];
        if(this.OldCount !== 0)
        {   
            const UpdatedCount = OldCount-1;

            const updatedIngredientCost = { ...this.state.ingredients}
            updatedIngredientCost[type] = UpdatedCount;

            const Oldcost = this.state.cost;
            const NewCost = Oldcost-IngredientCost[type];

            this.setState({cost : NewCost, ingredients: updatedIngredientCost });
        }
    }

    render()
    {
        return (
            <Aux>
                <Burger ingredients = {this.state.ingredients}/>
                <BurgerControls 
                    More = {this.IngredientAdded}
                    Less = {this.IngredientReduced}/>
            </Aux>
        );

    }
}

export default BurgerBuilder;
